#include <iostream> class Stack { // создаем класс стек, который реализует стек private: struct Node { int data; Node* next; Node(int data) : data(data), next(nullptr) {} }; Node* top; // Указатель на верхний элемент стека public: Stack() : top(nullptr) { } // Конструктор класса Stack, инициализирует указатель top как nullptr ~Stack() { while (!isEmpty()) { pop(); // Деструктор класса Stack, освобождает память, удаляя все // элементы стека } } void push(int data) { Node* newNode = new Node(data); newNode->next = top; top = newNode; } void pop() { if (isEmpty()) { std::cout << "Stack is empty." << std::endl; return; } Node* temp = top; top = top->next; delete temp; } int peek() { if (isEmpty()) { std::cout << "Stack is empty." << std::endl; return -1; } return top->data; } bool isEmpty() { return top == nullptr; }
}; int main() { Stack stack; // создаем экземпляр структуры for (int i = 0; i < 3; i++) { stack.push(i); // добавляем в стек числа от 0 до 2 } std::cout << "Верхний элемент: " << stack.peek() << "\n"; stack.pop(); // извлекаем элемент stack.pop(); // извлекаем элемент std::cout << "Top element: " << stack.peek() << "\n"; stack.pop(); // извлекаем элемент if (stack.isEmpty()) { // проверяем пуст ли стек std::cout << "Stack is empty."; }
}

