#include <iostream> class Queue { private: struct Node { int data; Node* next; Node(int data) : data(data), next(nullptr) {} }; Node* head; // указатель на голову очереди Node* tail; // указатель на хвост очереди public: Queue() : head(nullptr), tail(nullptr) { } // Конструктор Queue() инициализирует указатели head и tail значением // nullptr , чтобы указать, что очередь пустая ~Queue() { while (!isEmpty()) { dequeue(); // Деструктор ~Queue() освобождает память, выделенную для // каждого узла в очереди, путем последовательного вызова // функции dequeue() до тех пор, пока очередь не станет // пустой } } void enqueue(int data) { Node* newNode = new Node(data); if (isEmpty()) { head = tail = newNode; } else { tail->next = newNode; tail = newNode; } } void dequeue() { if (isEmpty()) { std::cout << "Queue is empty." << std::endl; return; } Node* temp = head; head = head->next; delete temp; if (head == nullptr) { tail = nullptr; } } int front() { if (isEmpty()) { std::cout << "Queue is empty." << std::endl; return -1; } return head->data; } bool isEmpty() { return head == nullptr; }
}; int main() { Queue queue; for (int i = 0; i < 3; i++) { queue.enqueue(i); // добавляем числа от 0 до 2 } std::cout << "Первый элемеент: " << queue.front() << "\n"; queue.dequeue(); // извлекаем элемент из очереди std::cout << "Первый элемент: " << queue.front() << "\n"; queue.dequeue(); // извлекаем элемент из очереди queue.dequeue(); // извлекаем элемент из очереди if (queue.isEmpty()) { // проверяем, пуста ли очередь std::cout << "Очередь пуста."; }
}

