#include <iostream>
using namespace std; class MaxHeap { private: int* heap; // Указатель на массив кучи int size; // Текущий размер кучи int capacity; // Емкость кучи (максимальный размер) public: MaxHeap(int capacity) { // Конструктор класса, инициализирует кучу this->capacity = capacity; // Сохраняем емкость кучи heap = new int[capacity]; // Выделяем память для массива кучи size = 0; // Изначально куча пуста } ~MaxHeap() { // Деструктор класса, освобождает память при уничтожении объекта delete[] heap; } void insert(int value) { // Вставка элемента в кучу if (size == capacity) { // Если куча полная, выбрасываем исключение throw "Heap is full"; } heap[size] = value; // Вставляем элемент в конец кучи int index = size; // Индекс вставленного элемента while (index > 0 && heap[(index - 1) / 2] < heap[index]) { // Пока не нарушается свойство кучи swap(heap[index], heap[(index - 1) / 2]); // Меняем элемент с его родителем index = (index - 1) / 2; // Переходим к родителю } size++; // Увеличиваем размер кучи } int extractMax() { // Извлечение максимального элемента из кучи if (size == 0) { // Если куча пуста, выбрасываем исключение throw "Heap is empty"; } int max = heap[0]; // Максимальный элемент - корень кучи heap[0] = heap[size - 1]; // Перемещаем последний элемент на место корня size--; // Уменьшаем размер кучи int index = 0; // Индекс текущего элемента while (index * 2 + 1 < size) { // Пока есть левый потомок int childIndex = index * 2 + 1; // Индекс левого потомка if (childIndex + 1 < size && heap[childIndex + 1] > heap[childIndex]) { // Если есть правый потомок и он больше // левого childIndex++; // Выбираем правого потомка } if (heap[index] >= heap[childIndex]) { // Если свойство кучи не нарушается break; } swap(heap[index], heap[childIndex]); // Меняем элемент с максимальным потомком index = childIndex; // Переходим к потомку } return max; // Возвращаем извлеченный максимальный элемент } int getSize() { // Возвращает текущий размер кучи return size; } bool isEmpty() { // Проверяет, пуста ли куча return size == 0; }
}; int main() { MaxHeap heap(10); // Создаем объект кучи с емкостью 10 heap.insert(3); // Вставляем элементы heap.insert(1); heap.insert(4); heap.insert(1); heap.insert(5); heap.insert(9); heap.insert(2); heap.insert(6); heap.insert(5); cout << "Max: " << heap.extractMax() << "\n"; // Извлекаем максимальный элемент cout << "Size: " << heap.getSize() << "\n"; // Выводим размер кучи cout << "Max: " << heap.extractMax() << "\n"; // Извлекаем максимальный элемент cout << "Size: " << heap.getSize() << "\n"; // Выводим размер кучи cout << "Empty: " << heap.isEmpty() << "\n"; // Проверяем, пуста ли куча
} Вывод: Max : 9
Size : 8
Max : 6
Size : 7
